//Get search term from Query params
var term = masterList.find(p => p.term === decodeURI(window.location.search.split('lowLevelTerm=')[1].replace(/\+/g, ' ')))

var sortable = [];
for (var vehicle in term) {
    sortable.push([vehicle, term[vehicle]]);
}

//Sort our tags by number of occurences
var tags = sortable.sort(function(a, b) {
    return a[1] - b[1];
});
tags = tags.reverse().slice(0, 10)
for (tag in tags) {
  let key = tags[tag][0]
  let value = tags[tag][1]
  tagsList.innerHTML += `<li><a href="/research-results-page?lowLevelTerm=${key}" class="button tag-list">${key} (${value})</a></li>`
}

